    public static int solve(int[] A, int B) {
        long count = 0;
        HashMap<Integer, Integer> map = new HashMap<>();
        for (int j : A) {
            if (map.containsKey(B - j)) {
                count += (map.get(B - j) % 1000000007);
                count %= 1000000007;
            }
            int freq = (map.getOrDefault(j, 0) + 1) % 1000000007;
            map.put(j, freq);
        }
        return (int) count % 1000000007;
    }
