    public static long solve(int[] A, int B) {
        int MOD = 1000000007;
        long[] freq = new long[B];
        for (int j : A) {
            int v = j % B;
            freq[v]++;
        }
        long ans;
        ans = (((freq[0] % MOD) * ((freq[0] - 1) % MOD)) % MOD) / 2;
        for (int i = 1; i <= ((B - 1) / 2); i++) {
            ans += (((freq[i] % MOD) * (freq[B - i] % MOD)) % MOD);
        }
        if (B % 2 == 0) {
            ans += ((freq[B / 2] % MOD) * ((freq[B / 2] - 1) % MOD) % MOD) / 2;
        }
        return ans;
    }

    public static long solveSolution2(int[] A, int B) {
        int MOD = 1000000007;
        long[] freq = new long[B];
        long ans = 0;
        for (int j : A) {
            int v = j % B;
            int pair;
            if (v == 0) {
                pair = 0;
            } else {
                pair = B - v;
            }
            ans += freq[pair];
            ans %= MOD;
            freq[v]++;
        }
        return ans;
    }
