    public static int solve(int[] A, int B) {
        long ans = 0, mod = 1000000007;
        int i = 0, j = A.length - 1;
        while (i < j) {
            int x = A[i], y = A[j];
            if (x == y) {
                if (x + y == B) {
                    long count = j - i + 1;
                    ans += count * (count - 1) / 2;
                }
                break;
            }
            if (x + y == B) {
                int count1 = 0, count2 = 0;
                while (A[i] == x) {
                    count1++;
                    i++;
                }
                while (A[j] == y) {
                    count2++;
                    j--;
                }
                long count = (long) count1 * count2;
                ans += count;
            } else if (x + y < B) {
                while (A[i] == x) {
                    i++;
                }
            } else {
                while (A[j] == y) {
                    j--;
                }
            }
        }
        return (int) (ans % mod);
    }
